mapper.select:
select
from sets_tbl LEFT JOIN app_user_tbl ON sets_tbl.user_id = app_user_tbl.user_id
where 1=1 AND app_user_tbl.user_id > 0 AND sets_tbl.user_id = #{userId,jdbcType=BIGINT}

mapper.insert:
insert into sets_tbl (id, user_id, name, 
  toothpaste, toothpaste_id, toothbrush, 
  toothbrush_id, mouthwash, mouthwash_id, 
  dentalfloss, dentalfloss_id, sales
  )
values (#{id,jdbcType=BIGINT}, #{userId,jdbcType=BIGINT}, #{name,jdbcType=VARCHAR}, 
  #{toothpaste,jdbcType=VARCHAR}, #{toothpasteId,jdbcType=BIGINT}, #{toothbrush,jdbcType=VARCHAR}, 
  #{toothbrushId,jdbcType=BIGINT}, #{mouthwash,jdbcType=VARCHAR}, #{mouthwashId,jdbcType=BIGINT}, 
  #{dentalfloss,jdbcType=VARCHAR}, #{dentalflossId,jdbcType=BIGINT}, #{sales,jdbcType=BIGINT}
  )
